import{d as a}from"./app.5b55431a.js";import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";const e={},p=a('<h1 id="\u7B2C\u4E00\u7BC7-\u9605\u8BFB\u7684\u5C42\u6B21" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u7BC7-\u9605\u8BFB\u7684\u5C42\u6B21" aria-hidden="true">#</a> \u7B2C\u4E00\u7BC7 \u9605\u8BFB\u7684\u5C42\u6B21</h1><h2 id="\u7B2C\u4E00\u7AE0-\u9605\u8BFB\u7684\u6D3B\u529B\u4E0E\u827A\u672F" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u7AE0-\u9605\u8BFB\u7684\u6D3B\u529B\u4E0E\u827A\u672F" aria-hidden="true">#</a> \u7B2C\u4E00\u7AE0 \u9605\u8BFB\u7684\u6D3B\u529B\u4E0E\u827A\u672F</h2><p>\u73B0\u4EE3\u7684\u5A92\u4F53\u6B63\u4E00\u4EE5\u538B\u5012\u6027\u7684\u6CDB\u6EE5\u8D44\u8BAF\u963B\u788D\u6211\u4EEC\u7684\u7406\u89E3\u529B\u3002\u4F1A\u4EA7\u751F\u8FD9\u4E2A\u73B0\u8C61\u7684\u4E00\u4E2A\u539F\u56E0\u662F\uFF1A\u6211\u4EEC\u6240\u63D0\u5230\u7684\u8FD9\u4E9B\u5A92\u4F53\uFF0C\u7ECF\u8FC7\u592A\u7CBE\u5BC6\u7684\u8BBE\u8BA1\uFF0C\u4F7F\u5F97\u601D\u60F3\u5F62\u540C\u6CA1\u6709\u9700\u8981\u4E86\u3002</p><h3 id="\u4E3B\u52A8\u7684\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u52A8\u7684\u9605\u8BFB" aria-hidden="true">#</a> \u4E3B\u52A8\u7684\u9605\u8BFB</h3><p>\u9605\u8BFB\u8D8A\u4E3B\u52A8\uFF0C\u6548\u679C\u8D8A\u597D\u3002\u8BFB\u8005\u5BF9\u5B83\u81EA\u5DF1\u4EE5\u53CA\u81EA\u5DF1\u9762\u524D\u7684\u4E66\u7C4D\uFF0C\u8981\u6C42\u7684\u8D8A\u591A\uFF0C\u83B7\u5F97\u7684\u5C31\u8D8A\u591A\u3002</p><p>\u62FF\u540C\u6837\u7684\u4E66\u7ED9\u4E0D\u540C\u7684\u4EBA\u9605\u8BFB\uFF0C\u4E00\u4E2A\u4EBA\u5374\u8BFB\u5F97\u6BD4\u53E6\u4E00\u4E2A\u4EBA\u597D\u8FD9\u4EF6\u4E8B\uFF0C\u9996\u5148\u5728\u4E8E\u8FD9\u4EBA\u7684\u9605\u8BFB\u66F4\u4E3B\u52A8\uFF0C\u5176\u6B21\uFF0C\u5728\u4E8E\u4ED6\u7684\u9605\u8BFB\u4E2D\u7684\u6BCF\u4E00\u79CD\u6D3B\u52A8\u90FD\u53C2\u4E0E\u4E86\u66F4\u591A\u7684\u6280\u5DE7\u3002</p><h3 id="\u9605\u8BFB\u7684\u76EE\u6807-\u4E3A\u83B7\u53D6\u8D44\u8BAF\u800C\u8BFB-\u4EE5\u53CA\u529B\u6C42\u5F97\u7406\u89E3\u800C\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u9605\u8BFB\u7684\u76EE\u6807-\u4E3A\u83B7\u53D6\u8D44\u8BAF\u800C\u8BFB-\u4EE5\u53CA\u529B\u6C42\u5F97\u7406\u89E3\u800C\u8BFB" aria-hidden="true">#</a> \u9605\u8BFB\u7684\u76EE\u6807\uFF1A\u4E3A\u83B7\u53D6\u8D44\u8BAF\u800C\u8BFB\uFF0C\u4EE5\u53CA\u529B\u6C42\u5F97\u7406\u89E3\u800C\u8BFB</h3><p>\u53EA\u6709\u4E00\u79CD\u65B9\u5F0F\u662F\u771F\u6B63\u5730\u5728\u9605\u8BFB\u3002\u6CA1\u6709\u4EFB\u4F55\u5916\u529B\u7684\u5E2E\u52A9\uFF0C\u4F60\u5C31\u662F\u8981\u8BFB\u8FD9\u672C\u4E66\u3002\u4F60\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF0C\u53EA\u51ED\u7740\u5185\u5FC3\u7684\u529B\u91CF\uFF0C\u73A9\u5473\u7740\u773C\u524D\u7684\u5B57\u53E5\uFF0C\u6162\u6162\u5730\u63D0\u5347\u81EA\u5DF1\uFF0C\u4ECE\u53EA\u6709\u6A21\u7CCA\u7684\u6982\u5FF5\u5230\u66F4\u6E05\u695A\u5730\u7406\u89E3\u4E3A\u6B62\u3002\u8FD9\u6837\u7684\u4E00\u79CD\u63D0\u5347\uFF0C\u662F\u5728\u9605\u8BFB\u65F6\u7684\u4E00\u79CD\u8111\u529B\u6D3B\u52A8\uFF0C\u4E5F\u662F\u66F4\u9AD8\u7684\u9605\u8BFB\u6280\u5DE7\u3002\u8FD9\u79CD\u9605\u8BFB\u5C31\u662F\u8BA9\u4E00\u672C\u4E66\u5411\u4F60\u65E2\u6709\u7684\u7406\u89E3\u529B\u505A\u6311\u6218\u3002</p><p>\u9605\u8BFB\u827A\u672F\u5B9A\u4E49\uFF1A\u8FD9\u662F\u4E00\u4E2A\u51ED\u501F\u7740\u5934\u8111\u8FD0\u4F5C\uFF0C\u9664\u4E86\u73A9\u5473\u8BFB\u7269\u4E2D\u7684\u4E00\u4E9B\u5B57\u53E5\u4E4B\u5916\uFF0C\u4E0D\u5047\u4EFB\u4F55\u5916\u52A9\uFF0C\u4EE5\u4E00\u5DF1\u4E4B\u529B\u6765\u63D0\u5347\u81EA\u6211\u7684\u8FC7\u7A0B\u3002</p><p>\u9605\u8BFB\u76EE\u7684\u5206\u4E3A\u4E24\u79CD\uFF0C\u4E00\u79CD\u662F\u9605\u8BFB\u62A5\u7EB8\u3001\u6742\u5FD7\uFF0C\u6216\u5176\u4ED6\u7684\u4E1C\u897F\uFF0C\u8FD9\u79CD\u9605\u8BFB\u80FD\u589E\u52A0\u6211\u4EEC\u7684\u54A8\u8BE2\uFF0C\u5374\u4E0D\u80FD\u589E\u52A0\u6211\u4EEC\u7684\u7406\u89E3\u529B\uFF0C\u56E0\u4E3A\u5728\u5F00\u59CB\u9605\u8BFB\u4E4B\u524D\uFF0C\u6211\u4EEC\u7684\u7406\u89E3\u529B\u5C31\u5DF2\u7ECF\u4E0E\u4ED6\u4EEC\u5B8C\u5168\u76F8\u5F53\u4E86\u3002\u53E6\u5916\u4E00\u79CD\u662F\u4E00\u4E2A\u4EBA\u8BD5\u7740\u8BFB\u67D0\u6837\u4ED6\u4E00\u5F00\u59CB\u5E76\u4E0D\u600E\u4E48\u4E86\u89E3\u7684\u4E1C\u897F\u3002\u8FD9\u4E2A\u4E1C\u897F\u7684\u6C34\u5E73\u5C31\u662F\u6BD4\u9605\u8BFB\u7684\u4EBA\u9AD8\u4E0A\u4E00\u622A\u3002\u8FD9\u4E2A\u4F5C\u8005\u60F3\u8981\u8868\u8FBE\u7684\u4E1C\u897F\uFF0C\u80FD\u589E\u8FDB\u9605\u8BFB\u8005\u7684\u7406\u89E3\u529B\u3002\u8FD9\u91CC\u7684\u201C\u5B66\u4E60\u201D\u6307\u7684\u662F\u7406\u89E3\u66F4\u591A\u7684\u4E8B\u60C5\uFF0C\u800C\u4E0D\u662F\u8BB0\u4F4F\u66F4\u591A\u7684\u54A8\u8BE2\u3002</p><p>\u6709\u4E24\u79CD\u60C5\u51B5\u6211\u4EEC\u4F1A\u4E3A\u4E86\u589E\u8FDB\u7406\u89E3\u800C\u9605\u8BFB\uFF0C\u7B2C\u4E00\u662F\u4E00\u5F00\u59CB\u65F6\u4E0D\u76F8\u7B49\u7684\u7406\u89E3\u7A0B\u5EA6\u3002\u7B2C\u4E8C\u662F\u9605\u8BFB\u7684\u4EBA\u4E00\u5B9A\u8981\u628A\u4E0D\u76F8\u7B49\u7684\u7406\u89E3\u529B\u514B\u670D\u5230\u4E00\u5B9A\u7A0B\u5EA6\u4E4B\u5185\u3002</p><h3 id="\u9605\u8BFB\u5C31\u662F\u5B66\u4E60-\u6307\u5BFC\u578B\u7684\u5B66\u4E60-\u4EE5\u53CA\u81EA\u6211\u53D1\u73B0\u578B\u7684\u5B66\u4E60\u4E4B\u95F4\u7684\u5DEE\u5F02" tabindex="-1"><a class="header-anchor" href="#\u9605\u8BFB\u5C31\u662F\u5B66\u4E60-\u6307\u5BFC\u578B\u7684\u5B66\u4E60-\u4EE5\u53CA\u81EA\u6211\u53D1\u73B0\u578B\u7684\u5B66\u4E60\u4E4B\u95F4\u7684\u5DEE\u5F02" aria-hidden="true">#</a> \u9605\u8BFB\u5C31\u662F\u5B66\u4E60\uFF1A\u6307\u5BFC\u578B\u7684\u5B66\u4E60\uFF0C\u4EE5\u53CA\u81EA\u6211\u53D1\u73B0\u578B\u7684\u5B66\u4E60\u4E4B\u95F4\u7684\u5DEE\u5F02</h3><p>\u8981\u80FD\u88AB\u542F\u53D1\uFF0C\u9664\u4E86\u77E5\u9053\u4F5C\u8005\u6240\u8BF4\u7684\u8BDD\u4E4B\u5916\uFF0C\u8FD8\u8981\u660E\u767D\u4ED6\u7684\u610F\u601D\uFF0C\u61C2\u5F97\u4ED6\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u4E48\u8BF4\u3002</p><p>\u6307\u5BFC\u578B\u7684\u5B66\u4E60\u4E0E\u81EA\u6211\u53D1\u73B0\u578B\u7684\u5B66\u4E60\u4E4B\u95F4\u7684\u5DEE\u5F02\u5728\u4E8E\u5B66\u4E60\u8005\u6240\u4F7F\u7528\u7684\u6559\u6750\u4E0A\u3002</p><h2 id="\u7B2C\u4E8C\u7AE0-\u9605\u8BFB\u7684\u5C42\u6B21" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7AE0-\u9605\u8BFB\u7684\u5C42\u6B21" aria-hidden="true">#</a> \u7B2C\u4E8C\u7AE0 \u9605\u8BFB\u7684\u5C42\u6B21</h2><p>\u4E00\u5171\u67094\u79CD\u9605\u8BFB\u7684\u5C42\u6B21</p><ol><li><p><strong>\u57FA\u7840\u9605\u8BFB\uFF08elementary reading\uFF09</strong>\u3002\u4E5F\u53EF\u4EE5\u7528\u5176\u4ED6\u7684\u540D\u79F0\uFF0C\u5982\u521D\u7EA7\u9605\u8BFB\u3001\u57FA\u672C\u9605\u8BFB\u6216\u521D\u6B65\u9605\u8BFB\u3002</p><p>\u5728\u8FD9\u4E2A\u5C42\u6B21\u7684\u9605\u8BFB\u4E2D\uFF0C\u8981\u95EE\u8BFB\u8005\u7684\u95EE\u9898\u662F\uFF1A\u201C\u8FD9\u4E2A\u53E5\u5B50\u5728\u8BF4\u4EC0\u4E48\uFF1F\u201D</p></li><li><p><strong>\u68C0\u89C6\u9605\u8BFB\uFF08inspectional reading\uFF09</strong>\u3002</p><p>\u5728\u4E00\u5B9A\u7684\u65F6\u95F4\u5185\uFF0C\u6293\u51FA\u4E00\u672C\u4E66\u7684\u91CD\u70B9\u3002\u8FD9\u4E2A\u5C42\u6B21\u8981\u95EE\u7684\u95EE\u9898\u662F\uFF1A\u201C\u8FD9\u672C\u4E66\u5728\u8C08\u4EC0\u4E48\uFF1F\u201D\u3001\u201C\u8FD9\u672C\u4E66\u7684\u67B6\u6784\u5982\u4F55\uFF1F\u201D</p></li><li><p><strong>\u5206\u6790\u9605\u8BFB\uFF08analytical reading\uFF09\u3002</strong></p><p>\u5206\u6790\u9605\u8BFB\u5C31\u662F\u5168\u76D8\u7684\u9605\u8BFB\u3001\u5B8C\u6574\u7684\u9605\u8BFB\uFF0C\u6216\u662F\u8BF4\u4F18\u8D28\u7684\u9605\u8BFB\uFF0C</p><p>\u5982\u679C\u4F60\u7684\u76EE\u6807\u53EA\u662F\u83B7\u5F97\u8D44\u8BAF\u6216\u6D88\u9063\uFF0C\u5C31\u5B8C\u5168\u6CA1\u6709\u5FC5\u8981\u7528\u5230\u5206\u6790\u9605\u8BFB\u3002<strong>\u5206\u6790\u9605\u8BFB\u64C5\u957F\u4E8E\u5BFB\u6C42\u7406\u89E3</strong>\u3002</p></li><li><p><strong>\u4E3B\u9898\u9605\u8BFB\uFF08syntopical reading\uFF09\u3002</strong></p><p>\u5728\u505A\u4E3B\u9898\u9605\u8BFB\u65F6\uFF0C\u9605\u8BFB\u8005\u4F1A\u8BFB\u5F88\u591A\u4E66\uFF0C\u800C\u4E0D\u662F\u4E00\u672C\u4E66\uFF0C\u5E76\u5217\u4E3E\u51FA\u8FD9\u4E9B\u4E66\u4E4B\u95F4\u76F8\u5173\u4E4B\u5904\uFF0C\u63D0\u51FA\u4E00\u4E2A\u6240\u6709\u7684\u4E66\u90FD\u8C08\u5230\u7684\u4E3B\u9898\u3002\u4E3B\u9898\u9605\u8BFB\u662F\u6700\u4E3B\u52A8\u3001\u4E5F\u6700\u82B1\u529B\u6C14\u7684\u4E00\u79CD\u9605\u8BFB\u3002</p></li></ol><h2 id="\u7B2C\u4E09\u7AE0-\u9605\u8BFB\u7684\u7B2C\u4E00\u4E2A\u5C42\u6B21-\u57FA\u7840\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u7AE0-\u9605\u8BFB\u7684\u7B2C\u4E00\u4E2A\u5C42\u6B21-\u57FA\u7840\u9605\u8BFB" aria-hidden="true">#</a> \u7B2C\u4E09\u7AE0 \u9605\u8BFB\u7684\u7B2C\u4E00\u4E2A\u5C42\u6B21\uFF1A\u57FA\u7840\u9605\u8BFB</h2><h3 id="\u5B66\u4E60\u9605\u8BFB\u7684\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60\u9605\u8BFB\u7684\u9636\u6BB5" aria-hidden="true">#</a> \u5B66\u4E60\u9605\u8BFB\u7684\u9636\u6BB5</h3><p>\u7B2C\u4E00\u9636\u6BB5\uFF0C\u201C\u9605\u8BFB\u51C6\u5907\u9636\u6BB5\u201D\uFF1B</p><p>\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u5B69\u5B50\u4F1A\u5B66\u4E60\u8BFB\u4E00\u4E9B\u7B80\u5355\u7684\u8BFB\u7269\uFF1B</p><p>\u7B2C\u4E09\u9636\u6BB5\u7684\u7279\u5F81\u662F\u5FEB\u901F\u5EFA\u7ACB\u5B57\u6C47\u7684\u80FD\u529B\uFF0C\u6240\u7528\u7684\u65B9\u6CD5\u662F\u4ECE\u4E0A\u4E0B\u6587\u6240\u63D0\u4F9B\u7684\u7EBF\u7D22\uFF0C\u201C\u63ED\u53D1\u201D\u4E0D\u719F\u6089\u7684\u5B57\u773C\uFF1B</p><p>\u7B2C\u56DB\u4E2A\u9636\u6BB5\u7684\u7279\u5F81\u662F\u7CBE\u70BC\u4E0E\u589E\u8FDB\u524D\u9762\u6240\u5B66\u7684\u6280\u5DE7\u3002\u4ECE\u4E00\u672C\u4E66\u6240\u63D0\u51FA\u6765\u7684\u4E00\u4E2A\u89C2\u70B9\u8F6C\u5316\u5230\u53E6\u4E00\u4E2A\u89C2\u70B9\uFF0C\u5728\u540C\u4E00\u4E2A\u4E3B\u9898\u4E0A\uFF0C\u5BF9\u4E0D\u540C\u7684\u4F5C\u8005\u6240\u63D0\u51FA\u7684\u89C2\u70B9\u4F5C\u6BD4\u8F83\u3002\u8FD9\u662F\u9605\u8BFB\u7684\u6210\u719F\u9636\u6BB5\uFF0C\u5E94\u8BE5\u662F\u4E00\u4E2A\u9752\u5C11\u5E74\u5C31\u8BE5\u8FBE\u5230\u7684\u5883\u754C\uFF0C\u4E5F\u662F\u7EC8\u5176\u4E00\u751F\u90FD\u8BE5\u6301\u7EED\u4E0B\u53BB\u7684\u3002</p><h3 id="\u9605\u8BFB\u7684\u9636\u6BB5\u4E0E\u5C42\u6B21" tabindex="-1"><a class="header-anchor" href="#\u9605\u8BFB\u7684\u9636\u6BB5\u4E0E\u5C42\u6B21" aria-hidden="true">#</a> \u9605\u8BFB\u7684\u9636\u6BB5\u4E0E\u5C42\u6B21</h3><p>\u4E0A\u9762\u6240\u63D0\u7684\u56DB\u4E2A\u9636\u6BB5\uFF0C\u90FD\u5C5E\u4E8E\u7B2C\u4E00\u4E2A\u5C42\u6B21\u7684\u9605\u8BFB\uFF0C\u57FA\u7840\u9605\u8BFB\u3002</p><p>\u57FA\u7840\u9605\u8BFB\u7684\u56DB\u4E2A\u9636\u6BB5\u4E00\u822C\u90FD\u6709\u4E00\u4F4D\u8001\u5E08\u5728\u65C1\u6307\u5BFC\u3002</p><h2 id="\u7B2C\u56DB\u7AE0-\u9605\u8BFB\u7684\u7B2C\u4E8C\u4E2A\u5C42\u6B21-\u68C0\u89C6\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u7AE0-\u9605\u8BFB\u7684\u7B2C\u4E8C\u4E2A\u5C42\u6B21-\u68C0\u89C6\u9605\u8BFB" aria-hidden="true">#</a> \u7B2C\u56DB\u7AE0 \u9605\u8BFB\u7684\u7B2C\u4E8C\u4E2A\u5C42\u6B21\uFF1A\u68C0\u89C6\u9605\u8BFB</h2><p>\u9664\u975E\u4F60\u80FD\u7CBE\u901A\u57FA\u7840\u9605\u8BFB\uFF0C\u5426\u5219\u4F60\u6CA1\u6CD5\u8FDB\u5165\u68C0\u89C6\u9605\u8BFB\u7684\u5C42\u6B21\u3002\u4F60\u5728\u9605\u8BFB\u4E00\u4F4D\u4F5C\u8005\u7684\u4F5C\u54C1\u65F6\u8981\u76F8\u5F53\u987A\u624B\uFF0C\u7528\u4E0D\u7740\u505C\u4E0B\u6765\u68C0\u67E5\u8BB8\u591A\u751F\u5B57\u7684\u610F\u601D\uFF0C\u4E5F\u4E0D\u4F1A\u88AB\u6587\u6CD5\u6216\u6587\u7AE0\u7ED3\u6784\u963B\u788D\u4F4F\u3002</p><h3 id="\u68C0\u89C6\u9605\u8BFB\u4E00-\u6709\u7CFB\u7EDF\u7684\u7565\u8BFB\u6216\u7C97\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u68C0\u89C6\u9605\u8BFB\u4E00-\u6709\u7CFB\u7EDF\u7684\u7565\u8BFB\u6216\u7C97\u8BFB" aria-hidden="true">#</a> \u68C0\u89C6\u9605\u8BFB\u4E00\uFF1A\u6709\u7CFB\u7EDF\u7684\u7565\u8BFB\u6216\u7C97\u8BFB</h3><p>\u7565\u8BFB\uFF0C\u5F53\u4F60\u6D4F\u89C8\u8FC7\u540E\uFF0C\u4F60\u53EF\u80FD\u4F1A\u53D1\u73B0\u8FD9\u672C\u4E66\u4EC5\u53EA\u662F\u5BF9\u4F60\u76EE\u524D\u6709\u7528\u800C\u5DF2\u3002\u8FD9\u672C\u4E66\u7684\u4EF7\u503C\u4E0D\u8FC7\u5982\u6B64\u800C\u5DF2\u3002\u4F46\u81F3\u5C11\u4F60\u77E5\u9053\u4F5C\u8005\u91CD\u8981\u7684\u4E3B\u5F20\u662F\u4EC0\u4E48\u4E86\uFF0C\u6216\u662F\u4ED6\u5230\u5E95\u5199\u7684\u662F\u600E\u6837\u7684\u4E00\u672C\u4E66\u3002\u56E0\u6B64\uFF0C\u4F60\u82B1\u5728\u7565\u8BFB\u8FD9\u672C\u4E66\u4E0A\u7684\u65F6\u95F4\u7EDD\u6CA1\u6709\u6D6A\u8D39\u3002</p><p>\u7565\u8BFB\u7684\u4E60\u60EF\u5E94\u8BE5\u4E0D\u7528\u82B1\u592A\u591A\u65F6\u95F4\uFF0C\u4EE5\u4E0B\u662F\u4E00\u4E9B\u5EFA\u8BAE\uFF1A</p><ol><li>**\u5148\u770B\u4E66\u540D\u9875\uFF0C\u7136\u540E\u5982\u679C\u6709\u5E8F\u5C31\u5148\u770B\u5E8F\u3002**\u8981\u5F88\u5FEB\u5730\u770B\u8FC7\u53BB\uFF0C\u7279\u522B\u6CE8\u610F\u526F\u6807\u9898\uFF0C\u6216\u5176\u4ED6\u7684\u76F8\u5173\u8BF4\u660E\u6216\u5B97\u65E8\uFF0C\u6216\u662F\u4F5C\u8005\u5199\u4F5C\u672C\u4E66\u7684\u7279\u6B8A\u89D2\u5EA6\u3002</li><li>**\u7814\u7A76\u76EE\u5F55\u9875\uFF0C**\u5BF9\u8FD9\u672C\u4E66\u7684\u57FA\u672C\u67B6\u6784\u505A\u6982\u62EC\u6027\u7684\u7406\u89E3\u3002</li><li><strong>\u5982\u679C\u4E66\u4E2D\u9644\u6709\u7D22\u5F15\uFF0C\u4E5F\u8981\u68C0\u9605\u4EE5\u4E0B</strong>\u3002</li><li><strong>\u8BFB\u4E00\u4E0B\u51FA\u7248\u8005\u7684\u4ECB\u7ECD\u3002</strong></li><li>\u4ECE\u4F60\u5BF9\u4E00\u672C\u4E66\u7684\u76EE\u5F55\u6982\u7565\uFF0C\u751A\u81F3\u6709\u70B9\u6A21\u7CCA\u7684\u5370\u8C61\u5F53\u4E2D\uFF0C**\u5F00\u59CB\u6311\u51E0\u4E2A\u770B\u8D77\u6765\u8DDF\u4E3B\u9898\u606F\u606F\u76F8\u5173\u7684\u7BC7\u7AE0\u6765\u770B\u3002**\u5982\u679C\u8FD9\u4E9B\u7BC7\u7AE0\u5728\u5F00\u5934\u6216\u7ED3\u5C3E\u6709\u6458\u8981\u8BF4\u660E\uFF08\u5F88\u591A\u4F1A\u6709\uFF09\uFF0C\u5C31\u8981\u4ED4\u7EC6\u5730\u9605\u8BFB\u8FD9\u4E9B\u8BF4\u660E\u3002</li><li>**\u6700\u540E\u4E00\u6B65\uFF0C\u628A\u4E66\u6253\u5F00\u6765\uFF0C\u4E1C\u7FFB\u7FFB\u897F\u7FFB\u7FFB\uFF0C\u5FF5\u4E2A\u4E00\u4E24\u6BB5\uFF0C\u6709\u65F6\u5019\u8FDE\u8BFB\u51E0\u9875\uFF0C\u4F46\u4E0D\u8981\u592A\u591A\u3002**\u5C31\u7528\u8FD9\u6837\u7684\u65B9\u6CD5\u628A\u5168\u4E66\u7FFB\u8FC7\u4E00\u904D\uFF0C\u968F\u65F6\u5BFB\u627E\u4E3B\u8981\u8BBA\u70B9\u7684\u8BAF\u53F7\uFF0C\u7559\u610F\u4E3B\u9898\u7684\u57FA\u672C\u8109\u7EDC\u3002</li></ol><p>\u8FD9\u662F\u4E00\u79CD\u975E\u5E38\u4E3B\u52A8\u7684\u9605\u8BFB\u3002\u4E00\u4E2A\u4EBA\u5982\u679C\u4E0D\u591F\u7075\u6D3B\uFF0C\u4E0D\u80FD\u591F\u96C6\u4E2D\u7CBE\u795E\u6765\u9605\u8BFB\uFF0C\u5C31\u6CA1\u6CD5\u8FDB\u884C\u68C0\u89C6\u9605\u8BFB\u3002</p><h3 id="\u68C0\u89C6\u9605\u8BFB\u4E8C-\u7C97\u6D45\u7684\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u68C0\u89C6\u9605\u8BFB\u4E8C-\u7C97\u6D45\u7684\u9605\u8BFB" aria-hidden="true">#</a> \u68C0\u89C6\u9605\u8BFB\u4E8C\uFF1A\u7C97\u6D45\u7684\u9605\u8BFB</h3><p><strong>\u5934\u4E00\u6B21\u9762\u5BF9\u4E00\u672C\u96BE\u8BFB\u7684\u4E66\u7684\u65F6\u5019\uFF0C\u4ECE\u5934\u5230\u5C3E\u5148\u8BFB\u5B8C\u4E00\u904D\uFF0C\u78B0\u5230\u4E0D\u61C2\u7684\u5730\u65B9\u4E0D\u8981\u505C\u4E0B\u6765\u67E5\u8BE2\u6216\u601D\u7D22\u3002</strong></p><p>\u53EA\u6CE8\u610F\u4F60\u80FD\u7406\u89E3\u7684\u90E8\u5206\uFF0C\u4E0D\u8981\u4E3A\u4E00\u4E9B\u8BBE\u6CD5\u7ACB\u5373\u4E86\u89E3\u7684\u4E1C\u897F\u800C\u505C\u987F\u3002\u7EE7\u7EED\u8BFB\u4E0B\u53BB\uFF0C\u7565\u8FC7\u54EA\u4E9B\u4E0D\u61C2\u7684\u90E8\u5206\uFF0C\u5F88\u5FEB\u4F60\u4F1A\u8BFB\u5230\u4F60\u770B\u5F97\u61C2\u7684\u5730\u65B9\u3002\u96C6\u4E2D\u7CBE\u795E\u5728\u8FD9\u4E2A\u90E8\u5206\uFF0C\u7EE7\u7EED\u8FD9\u6837\u8BFB\u4E0B\u53BB\u3002\u5C06\u5168\u4E66\u8BFB\u5B8C\uFF0C\u4E0D\u8981\u88AB\u4E00\u4E2A\u770B\u4E0D\u61C2\u7684\u7AE0\u8282\u3001\u6CE8\u89E3\u3001\u8BC4\u8BBA\u6216\u53C2\u8003\u8D44\u6599\u963B\u6320\u6216\u6CC4\u6C14\u3002\u5982\u679C\u4F60\u8BA9\u81EA\u5DF1\u88AB\u56F0\u4F4F\u4E86\uFF0C\u5982\u679C\u4F60\u5BB9\u6613\u81EA\u5DF1\u88AB\u67D0\u4E2A\u987D\u56FA\u7684\u6BB5\u843D\u7ED1\u4F4F\u4E86\uFF0C\u4F60\u5C31\u662F\u88AB\u6253\u8D25\u4E86\u3002</p><h3 id="\u9605\u8BFB\u7684\u901F\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u9605\u8BFB\u7684\u901F\u5EA6" aria-hidden="true">#</a> \u9605\u8BFB\u7684\u901F\u5EA6</h3><p>\u4F9D\u7167\u8BFB\u7269\u7684\u6027\u8D28\u4E0E\u590D\u6742\u7A0B\u5EA6\uFF0C\u7528\u4E0D\u540C\u7684\u901F\u5EA6\u6765\u9605\u8BFB\u3002</p><p>\u5206\u6790\u9605\u8BFB\u901A\u5E38\u6BD4\u68C0\u89C6\u9605\u8BFB\u6765\u5F97\u6162\u4E00\u4E9B\uFF0C\u4F46\u5C31\u7B97\u4F60\u62FF\u5230\u4E00\u672C\u4E66\u8981\u505A\u5206\u6790\u9605\u8BFB\uFF0C\u4E5F\u4E0D\u8BE5\u7528\u540C\u6837\u7684\u901F\u5EA6\u8BFB\u5B8C\u5168\u4E66\u3002\u6BCF\u4E00\u672C\u4E66\uFF0C\u4E0D\u8BBA\u662F\u591A\u4E48\u96BE\u8BFB\u7684\u4E66\uFF0C\u5728\u65E0\u5173\u7D27\u8981\u7684\u95F4\u9699\u90E8\u5206\u5C31\u53EF\u4EE5\u8BFB\u5FEB\u4E00\u70B9\u3002\u800C\u4E00\u672C\u597D\u4E66\uFF0C\u603B\u4F1A\u5305\u542B\u4E00\u4E9B\u6BD4\u8F83\u56F0\u96BE\uFF0C\u5E94\u8BE5\u6162\u6162\u9605\u8BFB\u7684\u5185\u5BB9\u3002</p><h3 id="\u9017\u7559\u4E0E\u5012\u9000" tabindex="-1"><a class="header-anchor" href="#\u9017\u7559\u4E0E\u5012\u9000" aria-hidden="true">#</a> \u9017\u7559\u4E0E\u5012\u9000</h3><p>\u672A\u53D7\u8FC7\u8BAD\u7EC3\u7684\u9605\u8BFB\u8005\uFF0C\u5728\u9605\u8BFB\u4E00\u884C\u5B57\u7684\u65F6\u5019\u4F1A\u5728\u4E94\u516D\u4E2A\u5730\u65B9\u53D1\u751F\u201C\u9017\u7559\u201D\uFF08fixate\uFF09\u73B0\u8C61\u3002\u6BCF\u770B\u8FC7\u4E24\u4E09\u884C\u4E4B\u540E\uFF0C\u773C\u775B\u5C31\u81EA\u7136\u5730\u201C\u5012\u9000\u201D\uFF08regress\uFF09\u5230\u539F\u70B9\u3002\u6240\u6709\u8FD9\u4E9B\u4E60\u60EF\u4E0D\u4F46\u6D6A\u8D39\u800C\u4E14\u663E\u7136\u964D\u4F4E\u4E86\u9605\u8BFB\u7684\u901F\u5EA6\u3002</p><p>\u4E4B\u6240\u4EE5\u8BF4\u662F\u6D6A\u8D39\uFF0C\u56E0\u4E3A\u6211\u4EEC\u7684\u5934\u8111\u8DDF\u773C\u775B\u4E0D\u4E00\u6837\uFF0C\u5E76\u4E0D\u9700\u8981\u4E00\u6B21\u53EA\u201C\u8BFB\u201D\u4E00\u4E2A\u5B57\u6216\u4E00\u4E2A\u53E5\u5B50\u3002\u6211\u4EEC\u7684\u5934\u8111\u662F\u4E2A\u60CA\u4EBA\u7684\u5DE5\u5177\uFF0C\u53EF\u4EE5\u5728\u201C\u4E00\u77A5\u201D\u4E4B\u95F4\u638C\u63E1\u4F4F\u4E00\u4E2A\u53E5\u5B50\u6216\u6BB5\u843D\u2014\u2014\u53EA\u8981\u773C\u775B\u80FD\u63D0\u4F9B\u8DB3\u591F\u7684\u54A8\u8BE2\u3002</p><p>\u8BAD\u7EC3\uFF1A\u5C06\u5927\u62C7\u6307\u4E0E\u98DF\u6307\u3001\u4E2D\u6307\u5408\u5E76\u5728\u4E00\u8D77\uFF0C\u7528\u8FD9\u4E2A\u201C\u6307\u9488\u201D\u987A\u7740\u4E00\u884C\u4E00\u884C\u7684\u5B57\u79FB\u52A8\u4E0B\u53BB\uFF0C\u901F\u5EA6\u8981\u6BD4\u4F60\u773C\u775B\u611F\u89C9\u7684\u8FD8\u8981\u5FEB\u4E00\u4E9B\u3002\u5F3A\u8FEB\u81EA\u5DF1\u7684\u773C\u775B\u8DDF\u7740\u624B\u90E8\u7684\u52A8\u4F5C\u79FB\u52A8\u3002\u4E00\u65E6\u4F60\u7684\u773C\u775B\u80FD\u8DDF\u7740\u624B\u79FB\u52A8\u65F6\uFF0C\u4F60\u5C31\u80FD\u8BFB\u5230\u90A3\u4E9B\u5B57\u53E5\u4E86\u3002\u7EE7\u7EED\u7EC3\u4E60\u4E0B\u53BB\uFF0C\u7EE7\u7EED\u589E\u5FEB\u624B\u7684\u52A8\u4F5C\uFF0C\u7B49\u5230\u4F60\u53D1\u89C9\u4EE5\u524D\uFF0C\u4F60\u7684\u901F\u5EA6\u5DF2\u7ECF\u53EF\u4EE5\u6BD4\u4EE5\u524D\u5FEB\u4E24\u4E09\u500D\u4E86\u3002</p><h3 id="\u7406\u89E3\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u7406\u89E3\u7684\u95EE\u9898" aria-hidden="true">#</a> \u7406\u89E3\u7684\u95EE\u9898</h3><p>\u901F\u8BFB\u7684\u95EE\u9898\u5C31\u51FA\u5728\u7406\u89E3\u529B\u4E0A\u3002\u8FD9\u91CC\u6240\u8C13\u7684\u7406\u89E3\u529B\u662F\u8D85\u8D8A\u57FA\u7840\u9605\u8BFB\u5C42\u6B21\u4EE5\u4E0A\u7684\u7406\u89E3\u529B\uFF0C\u4E5F\u662F\u9020\u6210\u95EE\u9898\u7684\u6839\u6E90\u3002</p><h3 id="\u68C0\u89C6\u9605\u8BFB\u7684\u6458\u8981" tabindex="-1"><a class="header-anchor" href="#\u68C0\u89C6\u9605\u8BFB\u7684\u6458\u8981" aria-hidden="true">#</a> \u68C0\u89C6\u9605\u8BFB\u7684\u6458\u8981</h3><p>\u66F4\u597D\u7684\u79D8\u65B9\u662F\uFF1A\u5728\u9605\u8BFB\u4E00\u672C\u4E66\u7684\u65F6\u5019\uFF0C\u6162\u4E0D\u8BE5\u6162\u5230\u4E0D\u503C\u5F97\uFF0C\u5FEB\u4E0D\u8BE5\u5FEB\u5230\u6709\u635F\u4E8E\u6EE1\u8DB3\u4E0E\u7406\u89E3\u3002</p><h2 id="\u7B2C\u4E94\u7AE0-\u5982\u4F55\u505A\u4E00\u4E2A\u81EA\u6211\u8981\u6C42\u7684\u8BFB\u8005" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u7AE0-\u5982\u4F55\u505A\u4E00\u4E2A\u81EA\u6211\u8981\u6C42\u7684\u8BFB\u8005" aria-hidden="true">#</a> \u7B2C\u4E94\u7AE0 \u5982\u4F55\u505A\u4E00\u4E2A\u81EA\u6211\u8981\u6C42\u7684\u8BFB\u8005</h2><h3 id="\u4E3B\u52A8\u7684\u9605\u8BFB\u57FA\u7840-\u4E00\u4E2A\u9605\u8BFB\u8005\u8981\u63D0\u51FA\u7684\u56DB\u4E2A\u57FA\u672C\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u52A8\u7684\u9605\u8BFB\u57FA\u7840-\u4E00\u4E2A\u9605\u8BFB\u8005\u8981\u63D0\u51FA\u7684\u56DB\u4E2A\u57FA\u672C\u95EE\u9898" aria-hidden="true">#</a> \u4E3B\u52A8\u7684\u9605\u8BFB\u57FA\u7840\uFF1A\u4E00\u4E2A\u9605\u8BFB\u8005\u8981\u63D0\u51FA\u7684\u56DB\u4E2A\u57FA\u672C\u95EE\u9898</h3><p><strong>\u4F60\u5728\u9605\u8BFB\u65F6\u8981\u63D0\u51FA\u95EE\u9898\u6765\u2014\u2014\u5728\u9605\u8BFB\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4F60\u81EA\u5DF1\u5FC5\u987B\u5C1D\u8BD5\u53BB\u56DE\u7B54\u7684\u95EE\u9898\u3002</strong></p><p>\u5173\u4E8E\u4E00\u672C\u4E66\uFF0C\u4F60\u4E00\u5B9A\u8981\u63D0\u51FA\u56DB\u4E2A\u4E3B\u8981\u7684\u95EE\u9898\uFF1A</p><ol><li><strong>\u6574\u4F53\u6765\u8BF4\uFF0C\u8FD9\u672C\u4E66\u5230\u5E95\u5728\u8C08\u4E9B\u4EC0\u4E48\uFF1F</strong></li><li><strong>\u4F5C\u8005\u7EC6\u90E8\u8BF4\u4E86\u4EC0\u4E48\uFF0C\u600E\u4E48\u8BF4\u7684\uFF1F</strong></li><li><strong>\u8FD9\u672C\u4E66\u8BF4\u5F97\u6709\u9053\u7406\u5417\uFF1F\u662F\u5168\u90E8\u6709\u9053\u7406\uFF0C\u8FD8\u662F\u90E8\u5206\u6709\u9053\u7406\uFF1F</strong></li><li><strong>\u8FD9\u672C\u4E66\u8DDF\u4F60\u6709\u4EC0\u4E48\u5173\u7CFB\uFF1F</strong></li></ol><p>\u8FD9\u56DB\u4E2A\u95EE\u9898\u662F\u9605\u8BFB\u7684\u57FA\u672C\u89C4\u5219\uFF0C\u8FD9\u662F\u7EDD\u4E0D\u53EF\u6216\u5FD8\u7684\u539F\u5219\uFF0C\u8FD9\u4E5F\u662F\u6709\u81EA\u6211\u8981\u6C42\u7684\u9605\u8BFB\u8005\u4E0E\u6CA1\u6709\u81EA\u6211\u8981\u6C42\u7684\u9605\u8BFB\u8005\u4E4B\u95F4\u5929\u58E4\u4E4B\u522B\u7684\u539F\u56E0\u3002\u8FD9\u4E2A\u539F\u5219\u9002\u7528\u4E8E\u4EFB\u4F55\u4E00\u79CD\u8BFB\u7269\u2014\u2014\u4E00\u672C\u4E66\u3001\u4E00\u7BC7\u6587\u7AE0\uFF0C\u751A\u81F3\u4E00\u4E2A\u5E7F\u544A\u3002</p><p>\u5149\u662F\u6307\u5BFC\u8FD9\u56DB\u4E2A\u95EE\u9898\u8FD8\u4E0D\u591F\u3002\u5728\u9605\u8BFB\u8FC7\u7A0B\u4E2D\uFF0C\u4F60\u8981\u8BB0\u5F97\u53BB\u63D0\u51FA\u8FD9\u4E9B\u95EE\u9898\u3002\u8981\u517B\u6210\u8FD9\u6837\u7684\u4E60\u60EF\uFF0C\u624D\u80FD\u79F0\u4E3A\u4E00\u4E2A\u6709\u81EA\u6211\u8981\u6C42\u7684\u9605\u8BFB\u8005\u3002\u9664\u6B64\u4E4B\u5916\uFF0C\u4F60\u8FD8\u8981\u77E5\u9053\u5982\u4F55\u7CBE\u51C6\u3001\u6B63\u786E\u5730\u56DE\u7B54\u95EE\u9898\u3002\u5982\u6B64\u8BAD\u7EC3\u800C\u6765\u7684\u80FD\u529B\uFF0C\u5C31\u662F\u9605\u8BFB\u7684\u827A\u672F\u3002</p><h3 id="\u5982\u4F55\u8BA9\u4E00\u672C\u4E66\u771F\u6B63\u5C5E\u4E8E\u4F60\u81EA\u5DF1" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u8BA9\u4E00\u672C\u4E66\u771F\u6B63\u5C5E\u4E8E\u4F60\u81EA\u5DF1" aria-hidden="true">#</a> \u5982\u4F55\u8BA9\u4E00\u672C\u4E66\u771F\u6B63\u5C5E\u4E8E\u4F60\u81EA\u5DF1</h3><p>\u9605\u8BFB\u65F6\u505A\u7B14\u8BB0\u7684\u597D\u5904\uFF1A</p><ol><li>\u8BA9\u4F60\u4FDD\u6301\u6E05\u9192</li><li>\u9605\u8BFB\uFF0C\u5982\u679C\u662F\u4E3B\u52A8\u7684\uFF0C\u5C31\u662F\u4E00\u79CD\u601D\u8003\uFF0C\u800C\u601D\u8003\u503E\u5411\u4E8E\u7528\u8BED\u8A00\u8868\u8FBE\u51FA\u6765</li><li>\u5C06\u4F60\u7684\u611F\u60F3\u5199\u4E0B\u6765\uFF0C\u80FD\u5E2E\u52A9\u4F60\u8BB0\u4F4F\u4F5C\u8005\u7684\u601D\u60F3\u3002</li></ol><p>\u505A\u7B14\u8BB0\u53EF\u4EE5\u91C7\u7528\u7684\u65B9\u6CD5\uFF1A</p><ol><li>\u753B\u5E95\u7EBF\u3002</li><li>\u5728\u753B\u5E95\u7EBF\u5904\u7684\u680F\u5916\u518D\u52A0\u753B\u4E00\u9053\u7EBF\u3002</li><li>\u5728\u7A7A\u767D\u5904\u505A\u661F\u53F7\u6216\u5176\u4ED6\u7B26\u53F7\u3002</li><li>\u5728\u7A7A\u767D\u5904\u7F16\u53F7\u3002</li><li>\u5728\u7A7A\u767D\u5904\u8BB0\u4E0B\u5176\u4ED6\u7684\u9875\u7801</li><li>\u5C06\u5173\u952E\u5B57\u6216\u53E5\u5B50\u5708\u51FA\u6765\u3002</li><li>\u5728\u4E66\u9875\u7684\u7A7A\u767D\u5904\u505A\u7B14\u8BB0\u3002</li></ol><p>\u4E09\u79CD\u505A\u7B14\u8BB0\u7684\u65B9\u6CD5</p><ul><li><strong>\u7ED3\u6784\u7B14\u8BB0\uFF08structural note-making\uFF09</strong>\uFF1A\u5728\u68C0\u89C6\u9605\u8BFB\u4E2D\uFF0C\u8981\u56DE\u7B54\u7684\u95EE\u9898\u662F\uFF1A1.\u8FD9\u662F\u4EC0\u4E48\u6837\u7684\u4E00\u672C\u4E66\uFF1F2.\u6574\u672C\u4E66\u5728\u8C08\u7684\u662F\u4EC0\u4E48\uFF1F3.\u4F5C\u8005\u662F\u501F\u7740\u600E\u6837\u7684\u6574\u4F53\u67B6\u6784\uFF0C\u6765\u53D1\u5C55\u4ED6\u7684\u89C2\u70B9\u6216\u9648\u8FF0\u4ED6\u8FD9\u4E2A\u4E3B\u9898\u7684\u7406\u89E3\u3002</li><li><strong>\u6982\u5FF5\u7B14\u8BB0\uFF08conceptual note-making\uFF09</strong></li><li><strong>\u8FA9\u8BC1\u7B14\u8BB0\uFF08dialectical note-making\uFF09</strong></li></ul><h1 id="\u7B2C\u4E8C\u7BC7-\u9605\u8BFB\u7684\u7B2C\u4E09\u4E2A\u5C42\u6B21-\u5206\u6790\u9605\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7BC7-\u9605\u8BFB\u7684\u7B2C\u4E09\u4E2A\u5C42\u6B21-\u5206\u6790\u9605\u8BFB" aria-hidden="true">#</a> \u7B2C\u4E8C\u7BC7 \u9605\u8BFB\u7684\u7B2C\u4E09\u4E2A\u5C42\u6B21\uFF1A\u5206\u6790\u9605\u8BFB</h1><h2 id="\u7B2C\u516D\u7AE0-\u4E00\u672C\u4E66\u7684\u5206\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516D\u7AE0-\u4E00\u672C\u4E66\u7684\u5206\u7C7B" aria-hidden="true">#</a> \u7B2C\u516D\u7AE0 \u4E00\u672C\u4E66\u7684\u5206\u7C7B</h2><h3 id="\u4E66\u7C4D\u5206\u7C7B\u7684\u91CD\u8981\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E66\u7C4D\u5206\u7C7B\u7684\u91CD\u8981\u6027" aria-hidden="true">#</a> \u4E66\u7C4D\u5206\u7C7B\u7684\u91CD\u8981\u6027</h3><p>\u5206\u6790\u9605\u8BFB\u7684\u7B2C\u4E00\u4E2A\u89C4\u5219\uFF1A<strong>\u4F60\u4E00\u5B9A\u8981\u77E5\u9053\u4F60\u81EA\u5DF1\u5728\u8BFB\u7684\u662F\u54EA\u4E00\u7C7B\u4E66\uFF0C\u800C\u4E14\u8D8A\u65E9\u77E5\u9053\u8D8A\u597D\u3002\u6700\u597D\u65E9\u5728\u4F60\u5F00\u59CB\u9605\u8BFB\u4E4B\u524D\u5C31\u5148\u77E5\u9053</strong>\u3002</p><h3 id="\u4ECE\u4E00\u672C\u4E66\u540D\u4E2D\u4F60\u80FD\u5B66\u5230\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u4E00\u672C\u4E66\u540D\u4E2D\u4F60\u80FD\u5B66\u5230\u4EC0\u4E48" aria-hidden="true">#</a> \u4ECE\u4E00\u672C\u4E66\u540D\u4E2D\u4F60\u80FD\u5B66\u5230\u4EC0\u4E48</h3><p>\u4E8B\u5B9E\u4E0A\uFF0C\u53EA\u6709\u5C11\u4E4B\u53C8\u5C11\u7684\u4EBA\u771F\u7684\u7528\u5FC3\u8BFB\u8FC7\u4E66\u540D\uFF0C\u4E5F\u60F3\u8FC7\u5176\u4E2D\u7684\u542B\u4E49\u3002</p><p>\u4E3A\u4E66\u7C4D\u8FDB\u884C\u5206\u7C7B\uFF0C\u901A\u5E38\u60C5\u51B5\u4E0B\u5F88\u96BE\u4ECE\u4E66\u540D\u4E2D\u770B\u51FA\u4E00\u4E2A\u4E66\u5C5E\u4E8E\u54EA\u4E00\u7C7B\u522B\uFF0C\u9700\u8981\u91C7\u7528\u68C0\u89C6\u9605\u8BFB\u7684\u65B9\u5F0F\u6765\u8FDB\u884C\u5206\u8FA8\u3002</p><h3 id="\u5B9E\u7528\u6027-vs-\u7406\u8BBA\u6027\u4F5C\u54C1" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u7528\u6027-vs-\u7406\u8BBA\u6027\u4F5C\u54C1" aria-hidden="true">#</a> \u5B9E\u7528\u6027 vs. \u7406\u8BBA\u6027\u4F5C\u54C1</h3><p>\u660E\u667A\u7684\u884C\u52A8\u6765\u81EA\u77E5\u8BC6\uFF0C\u77E5\u8BC6\u53EF\u4EE5\u7528\u5728\u8BB8\u591A\u65B9\u9762\uFF0C\u4E0D\u53EA\u662F\u63A7\u5236\u81EA\u7136\uFF0C\u53D1\u660E\u6709\u7528\u7684\u95EE\u9898\u6216\u5DE5\u5177\uFF0C\u8FD8\u53EF\u4EE5\u6307\u5BFC\u4EBA\u7C7B\u7684\u884C\u4E3A\uFF0C\u5728\u591A\u79CD\u6280\u672F\u9886\u57DF\u4E2D\u6821\u6B63\u4EBA\u7C7B\u7684\u8FD0\u4F5C\u6280\u5DE7\u3002</p><p>\u8981\u8BA9\u77E5\u8BC6\u53D8\u6210\u5B9E\u7528\uFF0C\u5C31\u8981\u6709\u64CD\u4F5C\u7684\u539F\u5219\u3002\u6211\u4EEC\u4E00\u5B9A\u8981\u8D85\u8D8A\u201C<strong>\u77E5\u9053\u8FD9\u662F\u600E\u4E48\u56DE\u4E8B</strong>\u201D\uFF0C\u8FDB\u800C\u660E\u767D\u201C<strong>\u5982\u679C\u6211\u4EEC\u60F3\u505A\u4E9B\u4EC0\u4E48\uFF0C\u5E94\u8BE5\u600E\u4E48\u5229\u7528\u5B83</strong>\u201D\u3002\u6982\u62EC\u6765\u8BF4\uFF0C\u8FD9\u4E5F\u662F\u77E5\u4E0E\u884C\u7684\u533A\u522B\u3002</p><p>\u5B9E\u7528\u7684\u4E66\u4F1A\u7ECF\u5E38\u51FA\u73B0\u201C\u5E94\u8BE5\u201D\u548C\u201C\u5E94\u5F53\u201D\u3001\u201C\u597D\u201D\u548C\u201C\u574F\u201D\u3001\u201C\u7ED3\u679C\u201D\u548C\u201C\u610F\u4E49\u201D\u4E4B\u7C7B\u7684\u5B57\u773C\u3002\u7406\u8BBA\u6027\u7684\u4F5C\u54C1\u5E38\u5E38\u8BF4\u201C\u662F\u201D\uFF0C\u6CA1\u6709\u201C\u5E94\u8BE5\u201D\u6216\u201C\u5E94\u5F53\u201D\u4E4B\u7C7B\u7684\u5B57\u773C\u3002</p><p>\u7406\u8BBA\u6027\u4F5C\u54C1\u7684\u5206\u7C7B</p><p>\u7406\u8BBA\u6027\u7684\u4F5C\u54C1\u4F1A\u88AB\u5206\u7C7B\u4E3A\u5386\u53F2\u3001\u79D1\u5B66\u548C\u54F2\u5B66\u7B49\u7B49\u3002</p><p>\u5386\u53F2\u5C31\u662F\u7EAA\u4E8B\uFF08Chronotopic\uFF09\u3002\u5386\u53F2\u5C31\u662F\u5728\u5904\u7406\u4E00\u4E9B\u53D1\u751F\u7684\u7279\u5B9A\u65F6\u95F4\uFF0C\u7279\u5B9A\u5730\u70B9\u7684\u771F\u5B9E\u4E8B\u4EF6\u3002</p><p>\u79D1\u5B66\u5219\u4E0D\u4F1A\u592A\u5728\u610F\u8FC7\u53BB\u7684\u4E8B\uFF0C\u5B83\u6240\u9762\u5BF9\u7684\u662F\u53EF\u80FD\u53D1\u751F\u5728\u4EFB\u4F55\u65F6\u95F4\u3001\u5730\u70B9\u7684\u4E8B\u3002\u79D1\u5B66\u5BB6\u5BFB\u6C42\u7684\u662F\u5B9A\u5F8B\u6216\u901A\u5219\u3002\u4ED6\u8981\u77E5\u9053\u5728\u6240\u6709\u7684\u60C5\u51B5\u6216\u5927\u591A\u7684\u60C5\u51B5\u4E2D\uFF0C\u4E8B\u60C5\u662F\u5982\u4F55\u53D1\u751F\u7684\u3002</p><p>\u54F2\u5B66\u6BD4\u8F83\u50CF\u79D1\u5B66\uFF0C\u5B83\u8FFD\u6C42\u7684\u662F\u4E00\u822C\u7684\u771F\u7406\u3002</p><p>\u5982\u679C\u4E00\u672C\u7406\u8BBA\u7684\u4E66\u6240\u5F3A\u8C03\u7684\u5185\u5BB9\uFF0C\u8D85\u4E4E\u4F60\u65E5\u5E38\u3001\u4F8B\u884C\u3001\u6B63\u5E38\u751F\u6D3B\u7684\u7ECF\u9A8C\uFF0C\u90A3\u5C31\u662F\u79D1\u5B66\u7684\u5B66\u3002\u5426\u5219\u5C31\u662F\u4E00\u672C\u54F2\u5B66\u7684\u4E66\u3002</p><h2 id="\u7B2C\u4E03\u7AE0-\u900F\u89C6\u4E00\u672C\u4E66" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E03\u7AE0-\u900F\u89C6\u4E00\u672C\u4E66" aria-hidden="true">#</a> \u7B2C\u4E03\u7AE0 \u900F\u89C6\u4E00\u672C\u4E66</h2><p>\u6BCF\u4E00\u672C\u4E66\u7684\u5C01\u9762\u4E4B\u4E0B\u90FD\u6709\u4E00\u5957\u81EA\u5DF1\u7684\u9AA8\u67B6\u3002\u4F5C\u4E3A\u4E00\u4E2A\u5206\u6790\u9605\u8BFB\u7684\u8BFB\u8005\uFF0C\u4F60\u7684\u8D23\u4EFB\u5C31\u662F\u8981\u627E\u51FA\u8FD9\u4E2A\u9AA8\u67B6\u3002</p><p>\u5206\u6790\u9605\u8BFB\u7684\u7B2C\u4E8C\u4E2A\u89C4\u5219\u662F\uFF1A<strong>\u4F7F\u7528\u4E00\u4E2A\u5355\u4E00\u7684\u53E5\u5B50\uFF0C\u6216\u6700\u591A\u51E0\u53E5\u8BDD\uFF08\u4E00\u5C0F\u6BB5\u6587\u5B57\uFF09\u6765\u53D9\u8FF0\u6574\u672C\u4E66\u7684\u5185\u5BB9</strong>\u3002</p><p>\u7B2C\u4E09\u4E2A\u89C4\u5219\uFF1A<strong>\u5C06\u4E66\u4E2D\u91CD\u8981\u7BC7\u7AE0\u5217\u4E3E\u51FA\u6765\uFF0C\u8BF4\u660E\u4ED6\u4EEC\u5982\u4F55\u6309\u7167\u987A\u5E8F\u7EC4\u6210\u4E00\u4E2A\u6574\u4F53\u7684\u67B6\u6784\u3002</strong></p><p>\u627E\u51FA\u4E00\u4E2A\u67B6\u6784\u662F\u8BFB\u8005\u7684\u8D23\u4EFB\uFF0C\u5C31\u8DDF\u5F53\u521D\u4F5C\u8005\u6709\u8D23\u4EFB\u81EA\u5DF1\u8BBE\u5B9A\u4E00\u4E2A\u67B6\u6784\u4E00\u6837\u3002\u4F46\u5E76\u4E0D\u9700\u8981\u4E3A\u6BCF\u672C\u4E66\u90FD\u5217\u51FA\u67B6\u6784\u3002</p><p>\u6CA1\u6709\u4E00\u672C\u4E66\u662F\u5B8C\u7F8E\u7684\u3002</p><p>\u8981\u5B8C\u6210\u7B2C\u4E8C\u4E2A\u89C4\u5219\uFF0C\u7B2C\u4E09\u4E2A\u89C4\u5219\u662F\u7EDD\u5BF9\u5FC5\u8981\u7684\u3002</p><p>\u7B2C\u56DB\u4E2A\u89C4\u5219\uFF1A**\u627E\u51FA\u4F5C\u8005\u8981\u95EE\u7684\u95EE\u9898\u3002**\u627E\u51FA\u6574\u672C\u4E66\u60F3\u8981\u89E3\u7B54\u7684\u95EE\u9898\u662F\u4EC0\u4E48\u3002</p><p>\u4EE5\u4E0A\u56DB\u4E2A\u89C4\u5219\u662F<strong>\u5206\u6790\u9605\u8BFB</strong>\u7684\u7B2C\u4E00\u4E2A\u9636\u6BB5\u3002</p><h2 id="\u7B2C\u516B\u7AE0-\u4E0E\u4F5C\u8005\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516B\u7AE0-\u4E0E\u4F5C\u8005\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49" aria-hidden="true">#</a> \u7B2C\u516B\u7AE0 \u4E0E\u4F5C\u8005\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49</h2><p>\u9664\u975E\u4F5C\u8005\u4E0E\u8BFB\u8005\u80FD\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49\uFF0C\u5426\u5219\u60F3\u8981\u628A\u77E5\u8BC6\u4ECE\u4E00\u65B9\u4F20\u9012\u5230\u53E6\u4E00\u65B9\u662F\u4E0D\u53EF\u80FD\u7684\u4E8B\u3002\u56E0\u4E3A\u8BCD\u4E49\uFF08term\uFF09\u662F\u53EF\u4F9B\u6C9F\u901A\u7684\u77E5\u8BC6\u7684\u57FA\u672C\u8981\u7D20\u3002\u53CC\u65B9\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49\u65F6\uFF0C\u6C9F\u901A\u5C31\u5B8C\u6210\u4E86\uFF0C\u4E24\u9897\u5FC3\u4E5F\u5947\u8FF9\u4F3C\u5730\u62E5\u6709\u4E86\u76F8\u540C\u7684\u60F3\u6CD5\u3002</p><p>\u5F53\u4E00\u4E2A\u5355\u5B57\u4F7F\u7528\u5F97\u6CA1\u6709\u6A21\u7CCA\u610F\u4E49\u7684\u65F6\u5019\uFF0C\u5C31\u662F\u4E00\u4E2A\u8BCD\u4E49\u4E86\u3002\u5F53\u67D0\u4E2A\u65F6\u95F4\uFF0C\u4F5C\u8005\u4E0E\u8BFB\u8005\u540C\u65F6\u5728\u4F7F\u7528\u540C\u4E00\u4E2A\u5355\u5B57\uFF0C\u5E76\u91C7\u53D6\u552F\u4E00\u76F8\u540C\u7684\u610F\u4E49\u65F6\uFF0C\u5728\u90A3\u79CD\u6BEB\u65E0\u6A21\u7CCA\u5730\u5E26\u7684\u72B6\u6001\u4E2D\uFF0C\u4ED6\u4EEC\u5C31\u662F\u627E\u51FA\u5171\u901A\u7684\u8BCD\u4E49\u4E86\u3002</p><p>\u89C4\u5219\u4E94\uFF1A<strong>\u627E\u51FA\u91CD\u8981\u5355\u5B57\uFF0C\u900F\u8FC7\u5B83\u4EEC\u4E0E\u4F5C\u8005\u8FBE\u6210\u5171\u8BC6\u3002</strong></p><p>\u6211\u4EEC\u53EF\u4EE5\u671F\u671B\u4E00\u4E2A\u597D\u4F5C\u8005\u5C3D\u53EF\u80FD\u7A7F\u8FC7\u8BED\u8A00\u6240\u65E0\u6CD5\u907F\u514D\u5F62\u6210\u7684\u969C\u788D\uFF0C\u548C\u6211\u4EEC\u63A5\u89E6\uFF0C\u4F46\u662F\u6211\u4EEC\u4E0D\u80FD\u671F\u671B\u53EA\u7531\u4ED6\u4E00\u4E2A\u4EBA\u6765\u505A\u8FD9\u6837\u7684\u5DE5\u4F5C\u3002\u6211\u4EEC\u5E94\u8BE5\u5728\u534A\u9014\u5C31\u8DDF\u4ED6\u76F8\u4F1A\u3002</p><p>\u9605\u8BFB\u4E00\u4E9B\u8FC7\u53BB\u4EBA\u6240\u5199\u7684\u4E66\uFF0C\u8981\u627E\u51FA\u4F5C\u8005\u5728\u5F53\u65F6\u65F6\u7A7A\u80CC\u666F\u4E0B\u7167\u5927\u591A\u6570\u4EBA\u4E60\u60EF\u800C\u4F7F\u7528\u7684\u90A3\u4E9B\u5B57\u773C\u7684\u610F\u601D\uFF0C\u5C31\u53EF\u80FD\u56F0\u96BE\u8BB8\u591A\u3002</p><p>\u4ECE\u4E00\u4E2A\u8BFB\u8005\u7684\u89D2\u5EA6\u6765\u770B\uFF0C\u6700\u91CD\u8981\u7684\u5B57\u5C31\u662F<strong>\u90A3\u4E9B\u8BA9\u4F60\u5934\u75BC\u7684\u5B57</strong>\u3002</p><p>\u5982\u679C\u8BFB\u8005\u78B0\u5230\u4E00\u4E2A\u4E0D\u4E86\u89E3\u7684\u5B57\u4E0D\u613F\u610F\u6DF1\u601D\uFF0C\u6216\u81F3\u5C11\u505A\u4E2A\u8BB0\u53F7\uFF0C\u90A3\u4ED6\u4E0D\u4E86\u89E3\u7684\u8FD9\u4E2A\u5B57\u5C31\u4E00\u5B9A\u4F1A\u7ED9\u4ED6\u5E26\u6765\u9EBB\u70E6\u3002</p><p>\u5F53\u5C06\u6709\u95EE\u9898\u7684\u5B57\u5708\u51FA\u6765\u540E\uFF0C\u8981\u5224\u65AD\u8FD9\u4E2A\u5B57\u662F\u6709\u4E00\u4E2A\u8FD8\u662F\u591A\u91CD\u610F\u4E49\u3002\u5982\u679C\u6709\u591A\u91CD\u610F\u4E49\uFF0C\u8981\u770B\u8FD9\u4E9B\u610F\u4E49\u4E4B\u95F4\u7684\u5173\u7CFB\u5982\u4F55\u3002\u6700\u540E\uFF0C\u8981\u6CE8\u610F\u8FD9\u4E9B\u5B57\u5728\u67D0\u4E2A\u5730\u65B9\u51FA\u73B0\u65F6\uFF0C\u4F7F\u7528\u7684\u662F\u5176\u4E2D\u54EA\u4E00\u79CD\u610F\u4E49\u3002\u770B\u770B\u4E0A\u4E0B\u6587\u662F\u5426\u6709\u4EFB\u4F55\u7EBF\u7D22\uFF0C\u53EF\u4EE5\u8BA9\u4F60\u660E\u767D\u53D8\u6362\u610F\u4E49\u7684\u7406\u7531\u3002</p><h2 id="\u7B2C\u4E5D\u7AE0-\u5224\u65AD\u4F5C\u8005\u7684\u4E3B\u65E8" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E5D\u7AE0-\u5224\u65AD\u4F5C\u8005\u7684\u4E3B\u65E8" aria-hidden="true">#</a> \u7B2C\u4E5D\u7AE0 \u5224\u65AD\u4F5C\u8005\u7684\u4E3B\u65E8</h2><p><strong>\u4F5C\u8005\u7684\u4E3B\u65E8\u5982\u679C\u6CA1\u6709\u7406\u8BBA\u7684\u652F\u6301\uFF0C\u5C31\u53EA\u662F\u5728\u6292\u53D1\u4E2A\u4EBA\u60F3\u6CD5\u7F62\u4E86\u3002</strong></p><p>\u5E76\u4E0D\u662F\u4E00\u672C\u4E66\u4E2D\u7684\u6BCF\u4E00\u53E5\u8BDD\u90FD\u5728\u8C08\u8BBA\u4E3B\u65E8\u3002\u6709\u65F6\u5019\uFF0C\u4E00\u4E9B\u53E5\u5B50\u5728\u8868\u8FBE\u7684\u662F\u7591\u95EE\u3002\u4ED6\u4EEC\u63D0\u51FA\u7684\u662F\u95EE\u9898\uFF0C\u800C\u4E0D\u662F\u7B54\u6848\u3002<strong>\u4E3B\u65E8\u5219\u662F\u8FD9\u4E9B\u95EE\u9898\u7684\u7B54\u6848</strong>\u3002</p><p>\u53EA\u8981\u6587\u5B57\u3001\u53E5\u5B50\u4E0E\u6BB5\u843D\u662F\u4E0D\u900F\u660E\u7684\u3001\u672A\u89E3\u6790\u7684\uFF0C\u4ED6\u4EEC\u5C31\u662F\u6C9F\u901A\u7684\u969C\u788D\uFF0C\u800C\u4E0D\u662F\u5A92\u4ECB\u3002\u4F60\u9605\u8BFB\u4E86\u4E00\u4E9B\u5B57\uFF0C\u5374\u6CA1\u6709\u83B7\u5F97\u77E5\u8BC6\u3002</p><p>\u89C4\u5219\u516D\uFF1A<strong>\u5C06\u4E00\u672C\u4E2D\u6700\u91CD\u8981\u7684\u53E5\u5B50\u5708\u51FA\u6765\uFF0C\u627E\u51FA\u5176\u4E2D\u7684\u4E3B\u65E8</strong>\u3002</p><p>\u89C4\u5219\u4E03\uFF1A<strong>\u4ECE\u76F8\u5173\u6587\u53E5\u7684\u5173\u8054\u4E2D\uFF0C\u8BBE\u6CD5\u67B6\u6784\u51FA\u4E00\u672C\u4E66\u7684\u57FA\u672C\u8BBA\u8FF0\u3002</strong></p><p>\u5982\u679C\u4F60\u5BF9\u4E00\u7BC7\u6587\u7AE0\u8FDE\u4E00\u4E2A\u95EE\u9898\u4E5F\u63D0\u4E0D\u51FA\u6765\uFF0C\u90A3\u4E48\u4F60\u5C31\u4E0D\u53EF\u80FD\u671F\u671B\u4E00\u672C\u4E66\u80FD\u7ED9\u4F60\u4E00\u4E9B\u4F60\u539F\u672C\u5C31\u6CA1\u6709\u7684\u89C6\u91CE\u3002</p><p>\u201C\u7528\u81EA\u5DF1\u7684\u8BDD\u6765\u8BF4\u201D\uFF0C\u662F\u6D4B\u9A8C\u4F60\u61C2\u4E0D\u61C2\u4E00\u4E2A\u53E5\u5B50\u7684\u4E3B\u65E8\u7684\u6700\u4F73\u65B9\u6CD5\u3002\u5982\u679C\u4F60\u65E0\u6CD5\u653E\u4E0B\u4F5C\u8005\u6240\u4F7F\u7528\u7684\u5B57\u53E5\uFF0C\u90A3\u8868\u793A\u4ED6\u6240\u4F20\u7ED9\u4F60\u7684\uFF0C\u53EA\u662F\u8FD9\u4E2A\u201C\u5B57\u201D\uFF0C\u800C\u4E0D\u662F\u4ED6\u7684\u201C\u601D\u60F3\u6216\u77E5\u8BC6\u201D\u3002\u53E6\u4E00\u4E2A\u6D4B\u9A8C\u7684\u65B9\u6CD5\u662F\uFF1A\u4F60\u80FD\u4E0D\u80FD\u4E3E\u51FA\u4E00\u4E2A\u81EA\u5DF1\u6240\u7ECF\u5386\u8FC7\u7684\u4E3B\u65E8\u6240\u5F62\u5BB9\u7684\u7ECF\u9A8C\uFF0C\u6216\u4E0E\u4E3B\u65E8\u6709\u67D0\u79CD\u76F8\u5173\u7684\u7ECF\u9A8C\uFF1F\u4F60\u80FD\u4E0D\u80FD\u5C31\u4F5C\u8005\u6240\u9610\u8FF0\u7684\u7279\u6B8A\u60C5\u51B5\uFF0C\u8BF4\u660E\u5176\u4E2D\u901A\u7528\u4E8E\u4E00\u822C\u7684\u9053\u7406\u3002\u5982\u679C\u4F60\u6CA1\u6CD5\u5C31\u8FD9\u4E2A\u4E3B\u65E8\u4E3E\u4EFB\u4F55\u4F8B\u5B50\u6216\u4F5C\u4EFB\u4F55\u8BF4\u660E\uFF0C\u4F60\u53EF\u80FD\u8981\u6000\u7591\u81EA\u5DF1\u5176\u5B9E\u5E76\u4E0D\u61C2\u8FD9\u4E2A\u53E5\u5B50\u5728\u8BF4\u4EC0\u4E48\u3002</p><p>\u73B0\u4EE3\u6559\u80B2\u5BB6\u6240\u72AF\u7684\u4E00\u4E2A\u6700\u5927\u7684\u9519\u8BEF\u5C31\u662F\u8FDD\u53CD\u4E86\u6559\u80B2\u7684\u827A\u672F\uFF0C\u4ED6\u4EEC\u53EA\u60F3\u8981\u80CC\u8BF5\u6587\u5B57\uFF0C\u6700\u540E\u5374\u9002\u5F97\u5176\u53CD\u3002</p><p>\u89C4\u5219\u4E03\u7684\u516C\u5F0F\uFF1A<strong>\u5982\u679C\u53EF\u4EE5\uFF0C\u627E\u51FA\u4E66\u4E2D\u8BF4\u660E\u91CD\u8981\u8BBA\u8FF0\u7684\u6BB5\u843D\u3002\u4F46\u662F\uFF0C\u5982\u679C\u8FD9\u4E2A\u8BBA\u8BC1\u5E76\u6CA1\u6709\u8FD9\u6837\u8868\u8FBE\u51FA\u6765\uFF0C\u4F60\u5C31\u8981\u53BB\u67B6\u6784\u51FA\u6765\u3002\u4F60\u8981\u4ECE\u8FD9\u4E00\u6BB5\u6216\u90A3\u4E00\u6BB5\u4E2D\u6311\u9009\u53E5\u5B50\u51FA\u6765\uFF0C\u7136\u540E\u6574\u7406\u51FA\u524D\u540E\u987A\u5E8F\u7684\u4E3B\u65E8\uFF0C\u4EE5\u53CA\u5176\u7EC4\u6210\u7684\u8BBA\u8FF0\u3002</strong></p><p>\u89C4\u5219\u516B\uFF1A<strong>\u627E\u51FA\u4F5C\u8005\u7684\u89E3\u7B54\u3002\u786E\u5B9A\u4F5C\u8005\u5DF2\u7ECF\u89E3\u51B3\u4E86\u54EA\u4E9B\u95EE\u9898\uFF0C\u8FD8\u6709\u54EA\u4E9B\u662F\u672A\u89E3\u51B3\u7684\u3002\u5728\u672A\u89E3\u51B3\u7684\u95EE\u9898\u4E2D\uFF0C\u786E\u5B9A\u54EA\u4E9B\u662F\u4F5C\u8005\u8BA4\u4E3A\u81EA\u5DF1\u65E0\u6CD5\u89E3\u51B3\u7684\u95EE\u9898</strong>\u3002</p><h2 id="\u7B2C\u5341\u7AE0-\u516C\u6B63\u5730\u8BC4\u65AD\u4E00\u672C\u4E66" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u7AE0-\u516C\u6B63\u5730\u8BC4\u65AD\u4E00\u672C\u4E66" aria-hidden="true">#</a> \u7B2C\u5341\u7AE0 \u516C\u6B63\u5730\u8BC4\u65AD\u4E00\u672C\u4E66</h2><p>\u5F53\u8BFB\u8005\u8BFB\u5B8C\u4E00\u672C\u4E66\uFF0C\u5FC5\u987B\u80FD\u8BC4\u8BBA\uFF0C\u6216\u63D0\u51FA\u6279\u8BC4\uFF0C\u624D\u7B97\u771F\u6B63\u7684\u8BFB\u5B8C\u8FD9\u672C\u4E66\u3002</p><p>\u4E00\u4E2A\u4EBA\u5982\u679C\u4E0D\u80FD\u81EA\u52A8\u81EA\u53D1\u5730\u8FD0\u7528\u72EC\u7ACB\u7684\u5224\u65AD\u529B\uFF0C\u4ED6\u6839\u672C\u5C31\u4E0D\u53EF\u80FD\u5B66\u5230\u4EFB\u4F55\u4E1C\u897F\u3002<strong>\u6700\u80FD\u5B66\u4E60\u7684\u8BFB\u8005\uFF0C\u4E5F\u5C31\u662F\u6700\u80FD\u6279\u8BC4\u7684\u8BFB\u8005\u3002</strong></p><p>\u5728\u505A\u8BF4\u670D\u5BF9\u65B9\u63A5\u53D7\u6211\u4EEC\u7684\u7406\u8BBA\u7684\u6C9F\u901A\u65F6\uFF0C\u63A5\u53D7\u7684\u4E00\u65B9\u5982\u679C\u4E5F\u60F3\u540C\u6837\u8BA4\u771F\uFF0C\u90A3\u5C31\u4E0D\u4F46\u8981\u6709\u56DE\u5E94\uFF0C\u8FD8\u8981\u505A\u4E00\u4E2A\u8D1F\u8D23\u7684\u503E\u542C\u8005\u3002\u4F60\u5BF9\u81EA\u5DF1\u6240\u542C\u5230\u7684\u8981\u6709\u56DE\u5E94\uFF0C\u8FD8\u8981\u6CE8\u610F\u5230\u5BF9\u65B9\u80CC\u540E\u7684\u610F\u56FE\u3002\u540C\u65F6\uFF0C\u4F60\u8FD8\u8981\u80FD\u6709\u81EA\u5DF1\u7684\u4E3B\u89C1\u3002\u5F53\u4F60\u6709\u81EA\u5DF1\u7684\u4E3B\u89C1\u65F6\uFF0C\u90A3\u5C31\u662F\u4F60\u7684\u4E3B\u5F20\uFF0C\u4E0D\u662F\u4F5C\u8005\u7684\u4E3B\u5F20\u3002\u5982\u679C\u4F60\u4E0D\u9760\u81EA\u5DF1\uFF0C\u53EA\u60F3\u4F9D\u8D56\u522B\u4EBA\u4E3A\u4F60\u505A\u5224\u65AD\uFF0C\u90A3\u4F60\u5C31\u662F\u5728\u505A\u5974\u96B6\uFF0C\u4E0D\u662F\u81EA\u7531\u7684\u4EBA\u4E86\u3002</p><p>\u5206\u6790\u9605\u8BFB\u7684\u7B2C\u4E09\u9636\u6BB5\u6700\u540E\u4E00\u5B9A\u8981\u8DDF\u7740\u524D\u4E24\u4E2A\u9636\u6BB5\u6765\u8FDB\u884C\u3002\u7B2C\u4E5D\u4E2A\u89C4\u5219\uFF1A<strong>\u5728\u4F60\u8BF4\u51FA\u201C\u6211\u540C\u610F\u201D\uFF0C\u201C\u6211\u4E0D\u540C\u610F\u201D\uFF0C\u6216\u201C\u6211\u6682\u7F13\u8BC4\u8BBA\u201D\u4E4B\u524D\uFF0C\u4F60\u4E00\u5B9A\u8981\u80FD\u80AF\u5B9A\u5730\u8BF4\uFF1A\u201C\u6211\u4E86\u89E3\u4E86\u3002\u201D</strong></p><p>\u89C4\u5219\u5341\uFF1A<strong>\u5F53\u4F60\u4E0D\u540C\u610F\u4F5C\u8005\u7684\u89C2\u70B9\u65F6\uFF0C\u8981\u7406\u6027\u5730\u8868\u8FBE\u81EA\u5DF1\u7684\u610F\u89C1\uFF0C\u4E0D\u8981\u65E0\u7406\u5730\u8FA9\u9A73\u6216\u4E89\u8BBA\u3002</strong></p><p>\u5728\u4E89\u8BBA\u4E4B\u4E2D\uFF0C\u4E00\u4E2A\u4EBA\u7EDD\u4E0D\u80FD\u5FD8\u4E86\u8FD9\u662F\u6559\u5BFC\u522B\u4EBA\uFF0C\u4E5F\u662F\u81EA\u5DF1\u53D7\u6559\u7684\u4E00\u4E2A\u673A\u4F1A\u3002</p><p>\u89C4\u5219\u5341\u4E00\uFF1A<strong>\u5C0A\u91CD\u77E5\u8BC6\u4E0E\u4E2A\u4EBA\u89C2\u70B9\u7684\u4E0D\u540C\uFF0C\u5728\u4F5C\u4EFB\u4F55\u8BC4\u65AD\u4E4B\u524D\uFF0C\u90FD\u8981\u627E\u51FA\u7406\u8BBA\u57FA\u7840\u3002</strong></p>',115);function i(n,h){return p}var s=r(e,[["render",i]]);export{s as default};
