import{e}from"./app.7d2acf8d.js";import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";const a={},c=e('<h2 id="service-worker" tabindex="-1"><a class="header-anchor" href="#service-worker" aria-hidden="true">#</a> Service Worker</h2><p>Service Worker \u662F\u8FD0\u884C\u5728\u6D4F\u89C8\u5668\u80CC\u540E\u7684\u72EC\u7ACB\u7EBF\u7A0B\uFF0C\u4E00\u822C\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u7F13\u5B58\u529F\u80FD\u3002\u4F7F\u7528 Service Worker\u7684\u8BDD\uFF0C\u4F20\u8F93\u534F\u8BAE\u5FC5\u987B\u4E3A HTTPS\u3002\u56E0\u4E3A Service Worker \u4E2D\u6D89\u53CA\u5230\u8BF7\u6C42\u62E6\u622A\uFF0C\u6240\u4EE5\u5FC5\u987B\u4F7F\u7528 HTTPS \u534F\u8BAE\u6765\u4FDD\u969C\u5B89\u5168\u3002<strong>Service Worker \u7684\u7F13\u5B58\u4E0E\u6D4F\u89C8\u5668\u5176\u4ED6\u5185\u5EFA\u7684\u7F13\u5B58\u673A\u5236\u4E0D\u540C\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u6211\u4EEC\u81EA\u7531\u63A7\u5236\u7F13\u5B58\u54EA\u4E9B\u6587\u4EF6\u3001\u5982\u4F55\u5339\u914D\u7F13\u5B58\u3001\u5982\u4F55\u8BFB\u53D6\u7F13\u5B58\uFF0C\u5E76\u4E14\u7F13\u5B58\u662F\u6301\u7EED\u6027\u7684</strong>\u3002</p><p>Service Worker \u5B9E\u73B0\u7F13\u5B58\u529F\u80FD\u4E00\u822C\u5206\u4E3A\u4E09\u4E2A\u6B65\u9AA4\uFF1A\u9996\u5148\u9700\u8981\u5148\u6CE8\u518C Service Worker\uFF0C\u7136\u540E\u76D1\u542C\u5230 install \u4E8B\u4EF6\u4EE5\u540E\u5C31\u53EF\u4EE5\u7F13\u5B58\u9700\u8981\u7684\u6587\u4EF6\uFF0C\u90A3\u4E48\u5728\u4E0B\u6B21\u7528\u6237\u8BBF\u95EE\u7684\u65F6\u5019\u5C31\u53EF\u4EE5\u901A\u8FC7\u62E6\u622A\u8BF7\u6C42\u7684\u65B9\u5F0F\u67E5\u8BE2\u662F\u5426\u5B58\u5728\u7F13\u5B58\uFF0C\u5B58\u5728\u7F13\u5B58\u7684\u8BDD\u5C31\u53EF\u4EE5\u76F4\u63A5\u8BFB\u53D6\u7F13\u5B58\u6587\u4EF6\uFF0C\u5426\u5219\u5C31\u53BB\u8BF7\u6C42\u6570\u636E\u3002</p><p>\u5F53 Service Worker \u6CA1\u6709\u547D\u4E2D\u7F13\u5B58\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u9700\u8981\u53BB\u8C03\u7528 fetch \u51FD\u6570\u83B7\u53D6\u6570\u636E\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5982\u679C\u6211\u4EEC\u6CA1\u6709\u5728 Service Worker \u547D\u4E2D\u7F13\u5B58\u7684\u8BDD\uFF0C\u4F1A\u6839\u636E\u7F13\u5B58\u67E5\u627E\u4F18\u5148\u7EA7\u53BB\u67E5\u627E\u6570\u636E\u3002\u4F46\u662F\u4E0D\u7BA1\u6211\u4EEC\u662F\u4ECE Memory Cache \u4E2D\u8FD8\u662F\u4ECE\u7F51\u7EDC\u8BF7\u6C42\u4E2D\u83B7\u53D6\u7684\u6570\u636E\uFF0C\u6D4F\u89C8\u5668\u90FD\u4F1A\u663E\u793A\u6211\u4EEC\u662F\u4ECE Service Worker \u4E2D\u83B7\u53D6\u7684\u5185\u5BB9\u3002</p><h2 id="memory-cache" tabindex="-1"><a class="header-anchor" href="#memory-cache" aria-hidden="true">#</a> Memory Cache</h2><p>Memory Cache \u4E5F\u5C31\u662F\u5185\u5B58\u4E2D\u7684\u7F13\u5B58\uFF0C\u4E3B\u8981\u5305\u542B\u7684\u662F\u5F53\u524D\u4E2D\u9875\u9762\u4E2D\u5DF2\u7ECF\u6293\u53D6\u5230\u7684\u8D44\u6E90,\u4F8B\u5982\u9875\u9762\u4E0A\u5DF2\u7ECF\u4E0B\u8F7D\u7684\u6837\u5F0F\u3001\u811A\u672C\u3001\u56FE\u7247\u7B49\u3002\u8BFB\u53D6\u5185\u5B58\u4E2D\u7684\u6570\u636E\u80AF\u5B9A\u6BD4\u78C1\u76D8\u5FEB,\u5185\u5B58\u7F13\u5B58\u867D\u7136\u8BFB\u53D6\u9AD8\u6548\uFF0C\u53EF\u662F\u7F13\u5B58\u6301\u7EED\u6027\u5F88\u77ED\uFF0C\u4F1A\u968F\u7740\u8FDB\u7A0B\u7684\u91CA\u653E\u800C\u91CA\u653E\u3002 <strong>\u4E00\u65E6\u6211\u4EEC\u5173\u95ED Tab \u9875\u9762\uFF0C\u5185\u5B58\u4E2D\u7684\u7F13\u5B58\u4E5F\u5C31\u88AB\u91CA\u653E\u4E86</strong>\u3002</p><p><strong>\u90A3\u4E48\u65E2\u7136\u5185\u5B58\u7F13\u5B58\u8FD9\u4E48\u9AD8\u6548\uFF0C\u6211\u4EEC\u662F\u4E0D\u662F\u80FD\u8BA9\u6570\u636E\u90FD\u5B58\u653E\u5728\u5185\u5B58\u4E2D\u5462\uFF1F</strong> \u8FD9\u662F\u4E0D\u53EF\u80FD\u7684\u3002\u8BA1\u7B97\u673A\u4E2D\u7684\u5185\u5B58\u4E00\u5B9A\u6BD4\u786C\u76D8\u5BB9\u91CF\u5C0F\u5F97\u591A\uFF0C\u64CD\u4F5C\u7CFB\u7EDF\u9700\u8981\u7CBE\u6253\u7EC6\u7B97\u5185\u5B58\u7684\u4F7F\u7528\uFF0C\u6240\u4EE5\u80FD\u8BA9\u6211\u4EEC\u4F7F\u7528\u7684\u5185\u5B58\u5FC5\u7136\u4E0D\u591A\u3002</p><p>\u5F53\u6211\u4EEC\u8BBF\u95EE\u8FC7\u9875\u9762\u4EE5\u540E\uFF0C\u518D\u6B21\u5237\u65B0\u9875\u9762\uFF0C\u53EF\u4EE5\u53D1\u73B0\u5F88\u591A\u6570\u636E\u90FD\u6765\u81EA\u4E8E\u5185\u5B58\u7F13\u5B58</p><p><img src="https://images-1256612942.cos.ap-guangzhou.myqcloud.com/bRZM0z.jpg" alt="bRZM0z"></p><p>\u5185\u5B58\u7F13\u5B58\u4E2D\u6709\u4E00\u5757\u91CD\u8981\u7684\u7F13\u5B58\u8D44\u6E90\u662Fpreloader\u76F8\u5173\u6307\u4EE4\uFF08\u4F8B\u5982<code>&lt;link rel=&quot;prefetch&quot;&gt;</code>\uFF09\u4E0B\u8F7D\u7684\u8D44\u6E90\u3002\u603B\u6240\u5468\u77E5preloader\u7684\u76F8\u5173\u6307\u4EE4\u5DF2\u7ECF\u662F\u9875\u9762\u4F18\u5316\u7684\u5E38\u89C1\u624B\u6BB5\u4E4B\u4E00\uFF0C\u5B83\u53EF\u4EE5\u4E00\u8FB9\u89E3\u6790js/css\u6587\u4EF6\uFF0C\u4E00\u8FB9\u7F51\u7EDC\u8BF7\u6C42\u4E0B\u4E00\u4E2A\u8D44\u6E90\u3002</p><p>\u9700\u8981\u6CE8\u610F\u7684\u4E8B\u60C5\u662F\uFF0C<strong>\u5185\u5B58\u7F13\u5B58\u5728\u7F13\u5B58\u8D44\u6E90\u65F6\u5E76\u4E0D\u5173\u5FC3\u8FD4\u56DE\u8D44\u6E90\u7684HTTP\u7F13\u5B58\u5934Cache-Control\u662F\u4EC0\u4E48\u503C\uFF0C\u540C\u65F6\u8D44\u6E90\u7684\u5339\u914D\u4E5F\u5E76\u975E\u4EC5\u4EC5\u662F\u5BF9URL\u505A\u5339\u914D\uFF0C\u8FD8\u53EF\u80FD\u4F1A\u5BF9Content-Type\uFF0CCORS\u7B49\u5176\u4ED6\u7279\u5F81\u505A\u6821\u9A8C</strong>\u3002</p><h2 id="disk-cache" tabindex="-1"><a class="header-anchor" href="#disk-cache" aria-hidden="true">#</a> Disk Cache</h2><p>Disk Cache \u4E5F\u5C31\u662F\u5B58\u50A8\u5728\u786C\u76D8\u4E2D\u7684\u7F13\u5B58\uFF0C\u8BFB\u53D6\u901F\u5EA6\u6162\u70B9\uFF0C\u4F46\u662F\u4EC0\u4E48\u90FD\u80FD\u5B58\u50A8\u5230\u78C1\u76D8\u4E2D\uFF0C<strong>\u6BD4\u4E4B Memory Cache \u80DC\u5728\u5BB9\u91CF\u548C\u5B58\u50A8\u65F6\u6548\u6027\u4E0A</strong>\u3002</p><p>\u5728\u6240\u6709\u6D4F\u89C8\u5668\u7F13\u5B58\u4E2D\uFF0CDisk Cache \u8986\u76D6\u9762\u57FA\u672C\u662F\u6700\u5927\u7684\u3002\u5B83\u4F1A\u6839\u636E HTTP Herder \u4E2D\u7684\u5B57\u6BB5\u5224\u65AD\u54EA\u4E9B\u8D44\u6E90\u9700\u8981\u7F13\u5B58\uFF0C\u54EA\u4E9B\u8D44\u6E90\u53EF\u4EE5\u4E0D\u8BF7\u6C42\u76F4\u63A5\u4F7F\u7528\uFF0C\u54EA\u4E9B\u8D44\u6E90\u5DF2\u7ECF\u8FC7\u671F\u9700\u8981\u91CD\u65B0\u8BF7\u6C42\u3002\u5E76\u4E14\u5373\u4F7F\u5728\u8DE8\u7AD9\u70B9\u7684\u60C5\u51B5\u4E0B\uFF0C\u76F8\u540C\u5730\u5740\u7684\u8D44\u6E90\u4E00\u65E6\u88AB\u786C\u76D8\u7F13\u5B58\u4E0B\u6765\uFF0C\u5C31\u4E0D\u4F1A\u518D\u6B21\u53BB\u8BF7\u6C42\u6570\u636E\u3002\u7EDD\u5927\u90E8\u5206\u7684\u7F13\u5B58\u90FD\u6765\u81EA Disk Cache\u3002</p><p><strong>\u6D4F\u89C8\u5668\u4F1A\u628A\u54EA\u4E9B\u6587\u4EF6\u4E22\u8FDB\u5185\u5B58\u4E2D\uFF1F\u54EA\u4E9B\u4E22\u8FDB\u786C\u76D8\u4E2D\uFF1F</strong> \u5173\u4E8E\u8FD9\u70B9\uFF0C\u7F51\u4E0A\u8BF4\u6CD5\u4E0D\u4E00\uFF0C\u4E0D\u8FC7\u4EE5\u4E0B\u89C2\u70B9\u6BD4\u8F83\u9760\u5F97\u4F4F\uFF1A</p><ul><li>\u5BF9\u4E8E\u5927\u6587\u4EF6\u6765\u8BF4\uFF0C\u5927\u6982\u7387\u662F\u4E0D\u5B58\u50A8\u5728\u5185\u5B58\u4E2D\u7684\uFF0C\u53CD\u4E4B\u4F18\u5148</li><li>\u5F53\u524D\u7CFB\u7EDF\u5185\u5B58\u4F7F\u7528\u7387\u9AD8\u7684\u8BDD\uFF0C\u6587\u4EF6\u4F18\u5148\u5B58\u50A8\u8FDB\u786C\u76D8</li></ul><h2 id="push-cache" tabindex="-1"><a class="header-anchor" href="#push-cache" aria-hidden="true">#</a> Push Cache</h2><p>Push Cache\uFF08\u63A8\u9001\u7F13\u5B58\uFF09\u662F HTTP/2 \u4E2D\u7684\u5185\u5BB9\uFF0C\u5F53\u4EE5\u4E0A\u4E09\u79CD\u7F13\u5B58\u90FD\u6CA1\u6709\u547D\u4E2D\u65F6\uFF0C\u5B83\u624D\u4F1A\u88AB\u4F7F\u7528\u3002<strong>\u5B83\u53EA\u5728\u4F1A\u8BDD\uFF08Session\uFF09\u4E2D\u5B58\u5728\uFF0C\u4E00\u65E6\u4F1A\u8BDD\u7ED3\u675F\u5C31\u88AB\u91CA\u653E\uFF0C\u5E76\u4E14\u7F13\u5B58\u65F6\u95F4\u4E5F\u5F88\u77ED\u6682</strong>\uFF0C\u5728Chrome\u6D4F\u89C8\u5668\u4E2D\u53EA\u67095\u5206\u949F\u5DE6\u53F3\uFF0C\u540C\u65F6\u5B83\u4E5F\u5E76\u975E\u4E25\u683C\u6267\u884CHTTP\u5934\u4E2D\u7684\u7F13\u5B58\u6307\u4EE4\u3002</p><ul><li>\u6240\u6709\u7684\u8D44\u6E90\u90FD\u80FD\u88AB\u63A8\u9001\uFF0C\u5E76\u4E14\u80FD\u591F\u88AB\u7F13\u5B58,\u4F46\u662F Edge \u548C Safari \u6D4F\u89C8\u5668\u652F\u6301\u76F8\u5BF9\u6BD4\u8F83\u5DEE</li><li>\u53EF\u4EE5\u63A8\u9001 no-cache \u548C no-store \u7684\u8D44\u6E90</li><li>\u4E00\u65E6\u8FDE\u63A5\u88AB\u5173\u95ED\uFF0CPush Cache \u5C31\u88AB\u91CA\u653E</li><li>\u591A\u4E2A\u9875\u9762\u53EF\u4EE5\u4F7F\u7528\u540C\u4E00\u4E2AHTTP/2\u7684\u8FDE\u63A5\uFF0C\u4E5F\u5C31\u53EF\u4EE5\u4F7F\u7528\u540C\u4E00\u4E2APush Cache\u3002\u8FD9\u4E3B\u8981\u8FD8\u662F\u4F9D\u8D56\u6D4F\u89C8\u5668\u7684\u5B9E\u73B0\u800C\u5B9A\uFF0C\u51FA\u4E8E\u5BF9\u6027\u80FD\u7684\u8003\u8651\uFF0C\u6709\u7684\u6D4F\u89C8\u5668\u4F1A\u5BF9\u76F8\u540C\u57DF\u540D\u4F46\u4E0D\u540C\u7684tab\u6807\u7B7E\u4F7F\u7528\u540C\u4E00\u4E2AHTTP\u8FDE\u63A5\u3002</li><li>Push Cache \u4E2D\u7684\u7F13\u5B58\u53EA\u80FD\u88AB\u4F7F\u7528\u4E00\u6B21</li><li>\u6D4F\u89C8\u5668\u53EF\u4EE5\u62D2\u7EDD\u63A5\u53D7\u5DF2\u7ECF\u5B58\u5728\u7684\u8D44\u6E90\u63A8\u9001</li><li>\u4F60\u53EF\u4EE5\u7ED9\u5176\u4ED6\u57DF\u540D\u63A8\u9001\u8D44\u6E90</li></ul><p>\u5982\u679C\u4EE5\u4E0A\u56DB\u79CD\u7F13\u5B58\u90FD\u6CA1\u6709\u547D\u4E2D\u7684\u8BDD\uFF0C\u90A3\u4E48\u53EA\u80FD\u53D1\u8D77\u8BF7\u6C42\u6765\u83B7\u53D6\u8D44\u6E90\u4E86\u3002</p><p>\u90A3\u4E48\u4E3A\u4E86\u6027\u80FD\u4E0A\u7684\u8003\u8651\uFF0C\u5927\u90E8\u5206\u7684\u63A5\u53E3\u90FD\u5E94\u8BE5\u9009\u62E9\u597D\u7F13\u5B58\u7B56\u7565\uFF0C<strong>\u901A\u5E38\u6D4F\u89C8\u5668\u7F13\u5B58\u7B56\u7565\u5206\u4E3A\u4E24\u79CD\uFF1A\u5F3A\u7F13\u5B58\u548C\u534F\u5546\u7F13\u5B58\uFF0C\u5E76\u4E14\u7F13\u5B58\u7B56\u7565\u90FD\u662F\u901A\u8FC7\u8BBE\u7F6E HTTP Header \u6765\u5B9E\u73B0\u7684</strong>\u3002</p><h2 id="\u7528\u6237\u884C\u4E3A\u5BF9\u6D4F\u89C8\u5668\u7F13\u5B58\u7684\u5F71\u54CD" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u884C\u4E3A\u5BF9\u6D4F\u89C8\u5668\u7F13\u5B58\u7684\u5F71\u54CD" aria-hidden="true">#</a> \u7528\u6237\u884C\u4E3A\u5BF9\u6D4F\u89C8\u5668\u7F13\u5B58\u7684\u5F71\u54CD</h2><p>\u6240\u8C13\u7528\u6237\u884C\u4E3A\u5BF9\u6D4F\u89C8\u5668\u7F13\u5B58\u7684\u5F71\u54CD\uFF0C\u6307\u7684\u5C31\u662F\u7528\u6237\u5728\u6D4F\u89C8\u5668\u5982\u4F55\u64CD\u4F5C\u65F6\uFF0C\u4F1A\u89E6\u53D1\u600E\u6837\u7684\u7F13\u5B58\u7B56\u7565\u3002\u4E3B\u8981\u6709 3 \u79CD\uFF1A</p><ul><li>\u6253\u5F00\u7F51\u9875\uFF0C\u5730\u5740\u680F\u8F93\u5165\u5730\u5740\uFF1A \u67E5\u627E disk cache \u4E2D\u662F\u5426\u6709\u5339\u914D\u3002\u5982\u6709\u5219\u4F7F\u7528\uFF1B\u5982\u6CA1\u6709\u5219\u53D1\u9001\u7F51\u7EDC\u8BF7\u6C42\u3002</li><li>\u666E\u901A\u5237\u65B0 (F5)\uFF1A\u56E0\u4E3A TAB \u5E76\u6CA1\u6709\u5173\u95ED\uFF0C\u56E0\u6B64 memory cache \u662F\u53EF\u7528\u7684\uFF0C\u4F1A\u88AB\u4F18\u5148\u4F7F\u7528(\u5982\u679C\u5339\u914D\u7684\u8BDD)\u3002\u5176\u6B21\u624D\u662F disk cache\u3002</li><li>\u5F3A\u5236\u5237\u65B0 (Ctrl + F5)\uFF1A\u6D4F\u89C8\u5668\u4E0D\u4F7F\u7528\u7F13\u5B58\uFF0C\u56E0\u6B64\u53D1\u9001\u7684\u8BF7\u6C42\u5934\u90E8\u5747\u5E26\u6709 <code>Cache-control: no-cache</code>(\u4E3A\u4E86\u517C\u5BB9\uFF0C\u8FD8\u5E26\u4E86 <code>Pragma: no-cache</code>),\u670D\u52A1\u5668\u76F4\u63A5\u8FD4\u56DE 200 \u548C\u6700\u65B0\u5185\u5BB9\u3002</li></ul>',24);function o(i,h){return c}var n=r(a,[["render",o]]);export{n as default};
